import styled from 'styled-components';

export const MovieInfo = styled.div`
  padding: 30px;
  font-weight: 500;
  color: #2a363b;
  font-size: 16px;
  max-width: 500px;
`;

export const MovieImage = styled.img`
  max-width: 300px;
`;

export const MovieDetails = styled.div`
  display: flex;
  margin-bottom: 20px;
`;

export const MovieInfoName = styled.div`
  font-weight: 700;
  font-size: 18px;
`;
