import styled from 'styled-components';

export const ButtonStyle = styled.button`
  margin-bottom: 20px;
  padding: 5px 10px;
  background-color: skyblue;
  color: black;
  border: none;
  &:hover {
    background-color: pink;
    color: white;
  }
`;
