import styled from 'styled-components';

export const ButtonStyle = styled.button`
  margin-bottom: 20px;
  padding: 5px 10px;
  background-color: plum;
  color: black;
  border: none;
  justify-content: center;
  align-items: center;
  display: flex;
  &:hover {
    background-color: pink;
    color: white;
  }
`;
