import styled from 'styled-components';

export const Button = styled.button`
  padding: 7px 10px;
  background-color: skyblue;
  color: black;
  border: none;
  &:hover {
    background-color: #2196f3;
    color: white;
  }
`;
export const Input = styled.input`
  padding: 5px 10px;
  outline: none;
`;

export const Form = styled.form`
  margin-bottom: 30px;
`;
